exports.getVersicherung = function (req, res, next) {
  var versicherungID = req.params.versicherungID;
  tdg.selectVersicherung(versicherungID,
    function(data){
      res.status(200).json({
        status: 'success',
        data: data,
      });
    },
    function(err){return next(err)}
  );
}
