var tdg = require('tabledatagateway.js');

var personID = 4;

var internal = new Object();

exports.getVersicherungOf = function (req, res, next) {
  tdg.selectVersicherungOf(personID,
    function(data){
      res.status(200).json({
        status: 'success',
        data: data,
      });
    },
    function(err){return next(err)}
  );
};

