//Einfache Select Queries
exports.getVersicherungByID = 'SELECT id, "personID", name, versicherungshoehe,'
  + ' beitrag, bezeichnung, "abschlussZeitpunkt", "kuendigungsZeitpunkt", "istGekuendigt"'
  + ' FROM "Versicherung" WHERE id=$1;'

exports.getVersicherungByPerson = 'SELECT id, "personID", name, versicherungshoehe,'
  + ' beitrag, bezeichnung, "abschlussZeitpunkt", "kuendigungsZeitpunkt", "istGekuendigt"'
  + ' FROM "Versicherung" WHERE "personID"=$1;'

exports.getVersicherungCountByPerson = 'SELECT count(*) as count FROM "Versicherung"'
  + ' WHERE "personID"=$1 GROUP BY "personID"'
