exports.versicherungID = function(req,res,next){
  var versicherungID = req.params.versicherungID;

  //Pr√ºfe ob die ID eine Nummer ist
  var regex = /^[1-9][0-9]*$/;
  if(!versicherungID.match(regex)){
    res.status(400).send('Fehlerhafte Anfrage: ID ist keine Zahl');
  } else{
    next();
  }
}
